import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { CommonModule } from '@angular/common';

import { AppRoutingModule } from './app-routing.module';
import { AppComponent } from './app.component';
import { PortfolioModule } from 'src/lib/portfolio.module';

@NgModule({
  declarations: [
    AppComponent,
  ],
  imports: [
    PortfolioModule,
    BrowserModule,
    CommonModule,
    AppRoutingModule,
  ],
  providers: [],
  bootstrap: [AppComponent]
})
export class AppModule { }

import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { BlogsComponent } from 'src/lib/blogs/blogs.component';
import { ProjectsComponent } from 'src/lib/projects/projects.component';
import { WallComponent } from 'src/lib/wall/wall.component';
import { SkillsComponent } from 'src/lib/skills/skills.component';
import { FlawsComponent } from 'src/lib/flaws/flaws.component';
import { PortfolioModule } from 'src/lib/portfolio.module';

const routes: Routes = [
  { path: "", component: WallComponent},
  { path: "projects", component: ProjectsComponent},
  { path: "blogs", component: BlogsComponent},
  { path: "skills", component: SkillsComponent},
  { path: "flaws", component: FlawsComponent},
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})

export class AppRoutingModule { }

import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { APP_BASE_HREF, CommonModule } from '@angular/common';
import { AppConfig } from 'src/config/app.config';
import { WallComponent } from 'src/lib/wall/wall.component';
import { BlogsComponent } from './blogs/blogs.component';
import { ProjectsComponent } from './projects/projects.component';
import { SkillsComponent } from './skills/skills.component';
import { FlawsComponent } from './flaws/flaws.component';
import { LoginComponent } from './auth/login/login.component';
import { SignupComponent } from './auth/signup/signup.component';
import { RouterModule } from '@angular/router';
import { FormsModule } from '@angular/forms';
import { HttpClientModule } from '@angular/common/http';
import { CookieService } from 'ngx-cookie-service';
import { AngularEditorModule } from '@kolkov/angular-editor';


@NgModule({
  declarations: [
    WallComponent, ProjectsComponent, BlogsComponent, SkillsComponent, FlawsComponent, LoginComponent, SignupComponent
  ],
  imports: [
    HttpClientModule, FormsModule, CommonModule, RouterModule.forChild([]), AngularEditorModule
  ],
  providers: [AppConfig, { provide: APP_BASE_HREF, useValue: '/' }, CookieService],
  exports: [ProjectsComponent, WallComponent, SkillsComponent, BlogsComponent, SignupComponent, LoginComponent],
  bootstrap: []
})
export class PortfolioModule { }

